        public void Draw()
        {
            Console.BackgroundColor = ConsoleColor.Gray;
            Console.ForegroundColor = ConsoleColor.Red;
            Console.Clear();
            Console.SetCursorPosition((Console.WindowWidth / 2) - (title.GetLength(1) / 2), 1);

            for (int y = 0; y < title.GetLength(0); y++)
            {
                Console.SetCursorPosition(Console.WindowWidth / 2 - title.GetLength(1) / 2, 1 + y);
                for (int x = 0; x < title.GetLength(1); x++)
                {
                    Console.Write(title[y, x]);
                }
            }
            Console.ForegroundColor = ConsoleColor.Black;
            Console.SetCursorPosition(3, title.GetLength(0) + 2);
            Console.WriteLine("Left/Right: Select Column. Space: Drop. Escape: Quit");
            Console.SetCursorPosition(19, title.GetLength(0) + 3);
            Console.WriteLine("Press Any Key To Start");
